$(function(){
	$(".special-menu").click(function(e){
		e.preventDefault();
		$(".special-menu").parent().find("ul").toggle();
	});
	$(".menu-btn").click(function(e){
		e.preventDefault();
		$("aside").toggle();
		$("main").toggleClass("no-aside");
	});

	$(window).bind("hashchange", function(){
		loadPage(location.hash.slice(3));
	});

	function loadPage(pageUrl){
		if(pageUrl === "") pageUrl = "home";
		document.title = pageUrl;
		pageUrl = "./pages/" + pageUrl + ".php";
		$(".main-load").empty().hide().load(pageUrl).fadeIn(500);
	}

	$("window").trigger('hashchange');

});